// src/pages/AddHabit.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { saveHabit } from '../services/storage';

const AddHabit = () => {
  const [name, setName] = useState('');
  const [goal, setGoal] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const newHabit = { name, goal: parseInt(goal, 10), progress: 0 };
    saveHabit(newHabit);
    navigate('/'); // Переход на главную страницу
  };

  return (
    <div className="add-habit">
      <h2>Добавить новую привычку</h2>
      <form onSubmit={handleSubmit}>
        <label>Название привычки:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <label>Цель (в днях):</label>
        <input
          type="number"
          value={goal}
          onChange={(e) => setGoal(e.target.value)}
          required
        />
        <button type="submit">Добавить привычку</button>
      </form>
    </div>
  );
};

export default AddHabit;
