import React, { useState } from 'react';
import './styles.css';
import AdviceCard from './components/AdviceCard';
import ThemeToggle from './components/ThemeToggle';

const adviceList = [
  "Не откладывай важное на завтра.",
  "Занимайся спортом хотя бы 15 минут в день.",
  "Учись на своих ошибках и растешь.",
  "Будь лучшей версией себя каждый день.",
  "Ты можешь больше, чем думаешь!"
];

function App() {
  const [theme, setTheme] = useState('light');
  const [advice, setAdvice] = useState(adviceList[Math.floor(Math.random() * adviceList.length)]);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const generateAdvice = () => {
    setAdvice(adviceList[Math.floor(Math.random() * adviceList.length)]);
  };

  return (
    <div className={`app ${theme}`}>
      <ThemeToggle toggleTheme={toggleTheme} />
      <AdviceCard advice={advice} generateAdvice={generateAdvice} />
    </div>
  );
}

export default App;
