import React, { useState } from 'react';
import './styles.css';
import AdviceCard from './components/AdviceCard';
import ThemeToggle from './components/ThemeToggle';
import Navbar from './components/Navbar';
import Favorite from './components/Favorite';
import Challenge from './components/Challenge';

const adviceList = [
  "Не откладывай важное на завтра.",
  "Занимайся спортом хотя бы 15 минут в день.",
  "Учись на своих ошибках и растешь.",
  "Будь лучшей версией себя каждый день.",
  "Ты можешь больше, чем думаешь!",
  "Верь в себя, даже когда никто не верит.",
  "Каждый день - новый шанс."
];

function App() {
  const [theme, setTheme] = useState('light');
  const [advice, setAdvice] = useState(adviceList[Math.floor(Math.random() * adviceList.length)]);
  const [favorites, setFavorites] = useState([]);
  const [challengeComplete, setChallengeComplete] = useState(false);
  const [adviceCount, setAdviceCount] = useState(0);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const generateAdvice = () => {
    setAdvice(adviceList[Math.floor(Math.random() * adviceList.length)]);
    setAdviceCount(prevCount => prevCount + 1);
  };

  const addToFavorites = () => {
    if (!favorites.includes(advice)) {
      setFavorites([...favorites, advice]);
    }
  };

  const completeChallenge = () => {
    if (adviceCount >= 5) {
      setChallengeComplete(true);
    } else {
      alert("Прочтите хотя бы 5 советов!");
    }
  };

  return (
    <div className={`app ${theme}`}>
      <Navbar />
      <div className="container">
        <ThemeToggle toggleTheme={toggleTheme} />
        <div className="content">
          <AdviceCard advice={advice} generateAdvice={generateAdvice} addToFavorites={addToFavorites} />
          <Favorite favorites={favorites} />
          <Challenge completeChallenge={completeChallenge} challengeComplete={challengeComplete} />
        </div>
      </div>
    </div>
  );
}

export default App;
