import React from 'react';

function Challenge({ completeChallenge, challengeComplete }) {
  return (
    <div className="challenge" id="challenges">
      <h3>Челленджи</h3>
      {!challengeComplete ? (
        <div>
          <p>Прочти 5 советов за сегодня</p>
          <button onClick={completeChallenge}>Завершить челлендж</button>
        </div>
      ) : (
        <p>Челлендж завершен! Молодец!</p>
      )}
    </div>
  );
}

export default Challenge;
