// src/pages/AddHabit.js

import React, { useState } from 'react';
import { saveHabit } from '../services/storage';
import { useNavigate } from 'react-router-dom';

const AddHabit = () => {
  const [name, setName] = useState('');
  const [goal, setGoal] = useState(0);
  const [progress, setProgress] = useState(0);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const newHabit = { name, goal, progress };
    saveHabit(newHabit);
    navigate('/');
  };

  return (
    <div className="add-habit">
      <h1>Добавить привычку</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Название привычки:</label>
          <input 
            type="text" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required
          />
        </div>
        <div>
          <label>Цель (дней):</label>
          <input 
            type="number" 
            value={goal} 
            onChange={(e) => setGoal(e.target.value)} 
            required
          />
        </div>
        <div>
          <label>Прогресс (дней):</label>
          <input 
            type="number" 
            value={progress} 
            onChange={(e) => setProgress(e.target.value)} 
            required
          />
        </div>
        <button type="submit">Добавить привычку</button>
      </form>
    </div>
  );
};

export default AddHabit;
