// src/components/HabitCard.js
import React from 'react';
import { updateProgress } from '../services/storage';

const HabitCard = ({ habit, index }) => {
  const handleProgressChange = () => {
    const newProgress = habit.progress + 1;
    updateProgress(index, newProgress);
  };

  return (
    <div className="habit-card">
      <h3>{habit.name}</h3>
      <p>Цель: {habit.goal} дней</p>
      <p>Прогресс: {habit.progress} / {habit.goal} дней</p>
      <button onClick={handleProgressChange}>Добавить день</button>
    </div>
  );
};

export default HabitCard;
