// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getHabits } from '../services/storage';

const Dashboard = () => {
  const [habits, setHabits] = useState([]);
  
  useEffect(() => {
    const storedHabits = getHabits();
    setHabits(storedHabits);
  }, []);

  const completedHabits = habits.filter(habit => habit.progress >= habit.goal).length;
  const totalHabits = habits.length;
  const overallProgress = totalHabits === 0 
    ? 0 
    : (habits.reduce((total, habit) => total + habit.progress, 0) / (habits.reduce((total, habit) => total + habit.goal, 0))) * 100;

  return (
    <div className="dashboard">
      <h1>Добро пожаловать в личный кабинет</h1>
      <div className="statistics">
        <div className="stat">
          <span>Всего привычек:</span>
          <strong>{totalHabits}</strong>
        </div>
        <div className="stat">
          <span>Выполнено привычек:</span>
          <strong>{completedHabits}</strong>
        </div>
        <div className="stat">
          <span>Общий прогресс:</span>
          <strong>{overallProgress.toFixed(2)}%</strong>
        </div>
      </div>
      <Link to="/add" className="add-habit-btn">Добавить привычку</Link>
      <div className="habits-list">
        {habits.map((habit, index) => (
          <div key={index} className="habit">
            <h3>{habit.name}</h3>
            <p>Цель: {habit.goal} дней</p>
            <p>Прогресс: {habit.progress} дней</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
