// src/pages/AddHabit.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Импортируем useNavigate
import { saveHabit } from '../services/storage';

const AddHabit = () => {
  const [name, setName] = useState('');
  const [goal, setGoal] = useState('');
  const navigate = useNavigate();  // Используем useNavigate для навигации

  const handleSubmit = (e) => {
    e.preventDefault();
    const newHabit = { name, goal: parseInt(goal, 10), progress: 0 };
    saveHabit(newHabit);
    navigate('/');  // Используем navigate вместо history.push
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Название привычки:</label>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <label>Цель (в днях):</label>
      <input
        type="number"
        value={goal}
        onChange={(e) => setGoal(e.target.value)}
        required
      />
      <button type="submit">Добавить привычку</button>
    </form>
  );
};

export default AddHabit;
