import React from 'react';

function AdviceCard({ advice, generateAdvice, addToFavorites }) {
  return (
    <div className="advice-card" id="advice">
      <p>{advice}</p>
      <div className="buttons">
        <button onClick={generateAdvice}>Новый совет</button>
        <button onClick={addToFavorites}>В Избранное</button>
      </div>
    </div>
  );
}

export default AdviceCard;
